import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      fullName: []
    };
  }

  componetDidMount() {
    this.getData();
  }

    componetDidMount() {
     if (!localStorage.getItem('fullName')) {
      this.setData();
     } else {
      console.log('using data from localStorage');

     }
    }

  componentWillUpdate(nextProps, nextState) {
    this.setData();
    localStorage.setItem("fullName", JSON.stringify(nextState.fullName));
  }

  handleSubmit = e => {
    e.preventDefault();
  };

  handleOnChange = e => {
    e.preventDefault();
    console.log(e.target.value);
    this.setState({
      fullName: e.target.value
    });
  };

  // local storage
  setData() {
    let leaderBoard = [];
    localStorage.setItem("fullName", JSON.stringify(leaderBoard));
  }

  getData() {
    let data = localStorage.getItem("fullName");
    data = JSON.parse(data);
  }

  render() {
    const { fullName } = this.state;
    return (
      <div>
        <h1>React Forms and Input</h1>
        <p>Full name: {fullName}</p>
        <form onSubmit={e => this.handleSubmit(e)}>
          <p>
            <input
              type="text"
              value={fullName}
              placeholder="your name"
              name="fullName"
              onChange={e => this.handleOnChange(e)}
            />
            {/* <button onClick={this.setData()}> Get Message </button> */}
            <button onClick={this.getData()}> Save </button>
          </p>
        </form>
      </div>
    );
  }
}

export default App;
